INSERT INTO `wp_es_pluginconfig` (`es_c_id`, `es_c_fromname`, `es_c_fromemail`, `es_c_mailtype`, `es_c_adminmailoption`, `es_c_adminemail`, `es_c_adminmailsubject`, `es_c_adminmailcontant`, `es_c_usermailoption`, `es_c_usermailsubject`, `es_c_usermailcontant`, `es_c_optinoption`, `es_c_optinsubject`, `es_c_optincontent`, `es_c_optinlink`, `es_c_unsublink`, `es_c_unsubtext`, `es_c_unsubhtml`, `es_c_subhtml`, `es_c_message1`, `es_c_message2`) VALUES
(1, 'Admin', 'shipraraimaths@gmail.com', 'WP HTML MAIL', 'YES', 'shipraraimaths@gmail.com', 'SRSC SHOP New email subscription', 'Hi Admin, \r\n\r\nWe have received a request to subscribe new email address to receive emails from our website. \r\n\r\nEmail: ###EMAIL### \r\nName : ###NAME### \r\n\r\nThank You\r\nSRSC SHOP', 'YES', 'SRSC SHOP Welcome to our newsletter', 'Hi ###NAME###, \r\n\r\nWe have received a request to subscribe this email address to receive newsletter from our website. \r\n\r\nThank You\r\nSRSC SHOP \r\n\r\n No longer interested in emails from SRSC SHOP?. Please <a href=\'###LINK###\'>click here</a> to unsubscribe', 'Double Opt In', 'SRSC SHOP confirm subscription', 'Hi ###NAME###, \r\n\r\nA subscription request for this email address was received. Please confirm it by <a href=\'###LINK###\'>clicking here</a>.\r\n\r\nIf you still cannot subscribe, please click this link : \r\n ###LINK### \r\n\r\nThank You\r\nSRSC SHOP', 'http://localhost/e_com/?es=optin&db=###DBID###&email=###EMAIL###&guid=###GUID###', 'http://localhost/e_com/?es=unsubscribe&db=###DBID###&email=###EMAIL###&guid=###GUID###', 'No longer interested in emails from SRSC SHOP?. Please <a href=\'###LINK###\'>click here</a> to unsubscribe', 'Thank You, You have been successfully unsubscribed. You will no longer hear from us.', 'Thank You, You have been successfully subscribed.', 'Oops.. This subscription cant be completed, sorry. The email address is blocked or already subscribed. Thank you.', 'Oops.. We are getting some technical error. Please try again or contact admin.');
INSERT INTO `wp_es_sentdetails` (`es_sent_id`, `es_sent_guid`, `es_sent_qstring`, `es_sent_source`, `es_sent_starttime`, `es_sent_endtime`, `es_sent_count`, `es_sent_preview`, `es_sent_status`, `es_sent_type`, `es_sent_subject`) VALUES
(1, 'gcyztq-yvcwko-pcorfu-zajuvl-shriye', '0', 'Post Notification', '2017-04-02 16:45:36', '2017-04-02 16:45:39', 2, 'Hello ###NAME###,<br /><br />We have published a new blog in our website. AMAZING GADGETS OF THIS ERA<br />Buy Latest Gadgets Here...<br />You may view the latest post at <a href=\'http://localhost/e_com/amazing-gadgets-of-this-era/\' target=\'_blank\'>http://localhost/e_com/amazing-gadgets-of-this-era/</a><br />You received this e-mail because you asked to be notified when new updates are posted.<br /><br />Thanks & Regards<br />Admin', 'Sent', 'Immediately', 'New post published AMAZING GADGETS OF THIS ERA'),
(2, 'swutac-nagrjd-ixqohk-fojmdz-ustaxy', '0', 'Post Notification', '2017-04-02 16:48:36', '2017-04-02 16:48:38', 2, 'Hello ###NAME###,<br /><br />We have published a new blog in our website. MAKES OUR WORK EASY - GADGETS<br />Life Is easy<br />You may view the latest post at <a href=\'http://localhost/e_com/makes-our-work-easy-gadgets/\' target=\'_blank\'>http://localhost/e_com/makes-our-work-easy-gadgets/</a><br />You received this e-mail because you asked to be notified when new updates are posted.<br /><br />Thanks & Regards<br />Admin', 'Sent', 'Immediately', 'New post published MAKES OUR WORK EASY - GADGETS');
INSERT INTO `wp_es_templatetable` (`es_templ_id`, `es_templ_heading`, `es_templ_body`, `es_templ_status`, `es_email_type`) VALUES
(1, 'New post published ###POSTTITLE###', 'Hello ###NAME###,\r\n\r\nWe have published a new blog in our website. ###POSTTITLE###\r\n###POSTDESC###\r\nYou may view the latest post at ###POSTLINK###\r\nYou received this e-mail because you asked to be notified when new updates are posted.\r\n\r\nThanks & Regards\r\nAdmin', 'Published', 'Post Notification'),
(2, 'Post notification ###POSTTITLE###', 'Hello ###EMAIL###,\r\n\r\nWe have published a new blog in our website. ###POSTTITLE###\r\n###POSTIMAGE###\r\n###POSTFULL###\r\nYou may view the latest post at ###POSTLINK###\r\nYou received this e-mail because you asked to be notified when new updates are posted.\r\n\r\nThanks & Regards\r\nAdmin', 'Published', 'Post Notification'),
(3, 'Hello World Newsletter', '<strong style="color: #990000"> Email Subscribers</strong><p>Email Subscribers plugin has options to send newsletters to subscribers. It has a separate page with HTML editor to create a HTML newsletter. Also have options to send notification email to subscribers when new posts are published to your blog. Separate page available to include and exclude categories to send notifications. Using plugin Import and Export options admins can easily import registered users and commenters to subscriptions list.</p> <strong style="color: #990000">Plugin Features</strong><ol> <li>Send notification email to subscribers when new posts are published.</li> <li>Subscription box.</li><li>Double opt-in and single opt-in facility for subscriber.</li> <li>Email notification to admin when user signs up (Optional).</li> <li>Automatic welcome mail to subscriber (Optional).</li> <li>Unsubscribe link in the mail.</li> <li>Import/Export subscriber emails.</li> <li>HTML editor to compose newsletter.</li> </ol> <p>Plugin live demo and video tutorial available on the official website. Check official website for more information.</p> <strong>Thanks & Regards</strong><br>Admin', 'Published', 'Newsletter');
